/*******************************************************************************
Teste da implementação do Algorithm 16 ML-KEM.Encaps(ek)
Uses the encapsulation key to generate a shared key and an associated ciphertext.
Validated input: encapsulation key ek ∈ B384k+32.
Output: shared key K ∈ B32.
Output: ciphertext c ∈ B32(duk+dv)
********************************************************************************/

void exibeEncaps(encaps resultados) {
    printf("\n Teste do algoritmo de Encapsulamento \n Texto Cifrado c : ");
    for (int i = 0; i < 32*(KYBER_DU*KYBER_K+KYBER_DV); i++)
    {
        printf("%02x", resultados.c[i]);
    }
    printf("\n \nchave  Simétrica K : ");
    for (int i = 0; i < sizeof(resultados.K)/sizeof(resultados.K[0]); i++)
    {
        printf("%02x", resultados.K[i]);
    }
}
void exibeResultadoG(encaps chaveK, uint8_t rValue[32]) {
    printf("\n\n Aplicando a função G(m∥H(ek)) para gerar K e r \n K : ");
    for (int i = 0; i < sizeof(chaveK.K)/sizeof(chaveK.K[0]); i++)
    {
        printf("%02x", chaveK.K[i]);
    }
    printf("\n \n Valor de r : ");
    for (int i = 0; i < 32; i++)
    {
        printf("%02x", rValue[i]);
    }
    printf("\n\n");
}

// Função para verificar o tamanho da chave de encapsulamento
int isValidEncapsSize(const uint8_t* ek, size_t size) {
    return (size == ENCAPS_SIZE);
}

encaps mlKemEncapsTemporario(uint8_t encapsKey[384*KYBER_K+32]) {
     if (!isValidEncapsSize(encapsKey, ENCAPS_SIZE)) {
        fprintf(stderr, "Erro: Tamanho da chave de encapsulamento inválido.\n");
        exit(EXIT_FAILURE);
    }
    // uint8_t m[32] = {0xf2, 0xe9, 0xd7, 0x31, 0x85, 0xee, 0x2a, 0x19, 0xbc, 0x8c, 0x8b, 0x2c, 0x1a, 0x39, 0xc6, 0x1b, 0xe7, 0xe5, 0x35, 0x61, 0xc4, 0xe7, 0xa2, 0xe2, 0xc3, 0x1f, 0x2a, 0x3e, 0xc3, 0xca, 0x59, 0xed};
    uint8_t m[32] = {0};
    generateRandomBytes(m,32);

    encaps kemEncaps = {0};
    // uint8_t r[32] = {0xcc, 0xfe, 0xfc, 0xdc, 0x94, 0x8a, 0x7a, 0xd0, 0xee, 0x26, 0xe4, 0x09, 0xb0, 0x84, 0x80, 0x64, 0x40, 0xd5, 0xbb, 0xa5, 0x36, 0x66, 0xe9, 0x43, 0xed, 0xec, 0x26, 0x6b, 0xaa, 0x44, 0x28, 0x41};
    uint8_t r[32] = {0};
    uint8_t mHek[64];
    uint8_t output[32];

    // (K,r) ← G(m∥H(ek)) ▷ derive shared secret key K and randomness r
    H(encapsKey,384*KYBER_K+32,output);
    memcpy(mHek,m,32);
    memcpy(mHek + 32, output,32);
    G(mHek,64,kemEncaps.K,r);
    exibeResultadoG(kemEncaps,r);

    // c ← K-PKE.Encrypt(ek,m,r) ▷ encrypt m using K-PKE with randomness r
    pkeEncrypt(encapsKey,m,r,kemEncaps.c);
    
    //return (K, c)
    return(kemEncaps);
}

